mapper.select:

	select * from (
	       select row_.*, rownum rownum_ from (       
	            select  from cs_info ci,cs_gk_info gk,cs_info_category ca


	           order by ${sort_name} ${sort_type}
	        ) row_ where rownum <=#{page_size,,jdbcType=INTEGER}+#{start_num,,jdbcType=INTEGER}       
    	) where rownum_ >=#{start_num,,jdbcType=INTEGER} + 1

select top ${page_size} from cs_info ci,cs_gk_info gk,cs_info_category ca
and ci.info_id not in (select top ${start_num} ci.info_id from cs_info ci,cs_gk_info gk,cs_info_category ca
order by ${sort_name} ${sort_type} ) order by ${sort_name} ${sort_type}

	select * from (
	       select row_.*, rownum rownum_ from (       
	            select   
	            from cs_info ci,cs_gk_info gk,cs_info_category ca 

	           order by ${orderby}

	        ) row_ where rownum <=#{page_size,,jdbcType=INTEGER}+#{start_num,,jdbcType=INTEGER}       
    	) where rownum_ >=#{start_num,,jdbcType=INTEGER} + 1

select   
            from cs_info ci,cs_gk_info gk,cs_info_category ca 

           order by ${orderby} limit #{start_num,,jdbcType=INTEGER},#{page_size,,jdbcType=INTEGER}

select top ${page_size} from cs_info ci,cs_gk_info gk,cs_info_category ca
and ci.info_id not in (select top ${start_num} ci.info_id from cs_info ci,cs_gk_info gk,cs_info_category ca
order by ${orderby} ) order by ${orderby}

