select * from (
       select row_.*, rownum rownum_ from (       
	    select ci.info_id,ci.title,ci.thumb_url,ci.content_url,ci.model_id,ci.released_dtime from cs_info ci,cs_gk_info gk 
	    where ci.site_id = #{site_id} and ${keyword_con} and ci.info_status = 8 and ci.final_status = 0 and ci.info_id != ${info_id} and ci.info_id = gk.info_id
	   order by ci.info_id desc

	) row_ where rownum <=#{page_size,,jdbcType=INTEGER}+#{start_num,,jdbcType=INTEGER}       
) where rownum_ >=#{start_num,,jdbcType=INTEGER} + 1

select * from (
    select * from (
    select row_.*, rownum rownum_ from (
    select ci.info_id,ci.title,ci.thumb_url,ci.content_url,ci.model_id,ci.released_dtime from cs_info ci,cs_gk_info gk
    where ci.site_id = #{site_id} and ${keyword_con} and ci.info_status = 8 and ci.final_status = 0 and ci.info_id != ${info_id} and ci.info_id = gk.info_id and  ci.released_dtime <= #{released_dtime} 
    order by ci.released_dtime desc

            ) row_ where rownum <=${page_size}+${start_num}
        ) where rownum_ >=${start_num} + 1

) as t1
UNION
select * from (
    select * from (
    select row_.*, rownum rownum_ from (
    select ci.info_id,ci.title,ci.thumb_url,ci.content_url,ci.model_id,ci.released_dtime from cs_info ci,cs_gk_info gk
    where ci.site_id = #{site_id} and ${keyword_con} and ci.info_status = 8 and ci.final_status = 0 and ci.info_id != ${info_id} and ci.info_id = gk.info_id and  ci.released_dtime >= #{released_dtime} 
    order by ci.released_dtime asc

            ) row_ where rownum <=${page_size}+${start_num}
        ) where rownum_ >=${start_num} + 1

) as t2
