mapper.select:
        select count(*) count, is_host from cs_info where
        final_status=0
        and info_status =8
        and site_id = #{site_id}

            and cat_id in (${cat_id})


            and input_user = #{input_user}



			and released_dtime >= #{start_day}




  			and released_dtime <= #{end_day}


        group by is_host

        select i.count, u.user_id, u.user_realname,u.sort from (
        select count(*) count, input_user from cs_info where
        final_status=0

            and info_status = #{info_status}


            and cat_id in (${cat_ids})


            and is_host in (${is_host})



			and input_dtime >= #{start_day}




  			and input_dtime <= #{end_day}




  			and is_pic = #{is_pic}


        and site_id = #{site_id}
        group by input_user
        ) i right join cs_org_user u on i.input_user = u.user_id
        order by ${sort_type}

select i.count, i.input_user, u.me_realname from (
select count(*) count, input_user from cs_info where
final_status=0

    and info_status = #{info_status}


    and cat_id in (${cat_ids})


    and is_host in (${is_host})



and input_dtime >= #{start_day}




and input_dtime <= #{end_day}




and is_pic = #{is_pic}



and site_id = #{site_id}
group by input_user
) i,cs_member u where i.input_user = u.me_id

    and mcat_id = #{mcat_id}

order by count

        select i.count, i.cat_id, g.cat_cname from
        (select count(*) count, cat_id from cs_info where
        final_status=0 and info_status = 8
        and site_id = #{site_id}

            and input_user = #{input_user}


            and info_status = #{info_status}


            and cat_id in (${cat_ids})


            and is_host in (${is_host})



			and released_dtime >= #{start_day}




  			and released_dtime <= #{end_day}




  			and is_pic = #{is_pic}


        group by cat_id
        ) i, cs_info_category g where i.cat_id = g.cat_id and site_id = #{site_id}

select count(*) count, is_host,released_dtime from ( select info_id, is_host
, SUBSTR(released_dtime,0,10) released_dtime
, SUBSTR(released_dtime,0,7) released_dtime
from cs_info where final_status=0 and info_status = 8 and site_id = #{site_id}
and cat_id in (${cat_ids})
and released_dtime >= #{start_day}
and released_dtime <= #{end_day}
) group by is_host, released_dtime order by released_dtime desc

select count(*) count, t.is_host,t.released_dtime from ( select info_id, is_host
, left(released_dtime,10) released_dtime
, left(released_dtime,7) released_dtime
from cs_info where 1=1
and cat_id in (${cat_ids})
and final_status=0 and info_status =8 and site_id = #{site_id}
and released_dtime >= #{start_day}
and released_dtime <= #{end_day}
) t group by t.is_host, t.released_dtime order by t.released_dtime desc

