select row_.*, rownum rownum_ from (select count(info_id) as icount,cat_id from cs_info_access where site_id =
#{site_id}
 and access_time >= #{start_day}
 and access_time <= #{end_day}
group by cat_id order by icount desc) row_ where
 rownum <= ${row_count}

select count(info_id) as icount,cat_id from cs_info_access where site_id = #{site_id}
 and access_time >= #{start_day}
 and access_time <= #{end_day}
group by cat_id order by icount desc
 limit ${row_count}

