select
ci.title,ci.is_pic,ci.info_id,ci.cat_id,ci.thumb_url,ci.content_url,ci.input_user,ci.input_dtime,ci.info_status,ci.source
from cs_info ci,cs_info_category ca where ci.cat_id = ca.cat_id and ci.site_id = ca.site_id and ci.final_status
= 0
and ci.site_id = #{site_id}
and ci.input_user = #{input_user}
and ci.info_status = #{info_status}
and ci.cat_id = #{cat_id}
and ci.is_host in (${is_host})
 and ci.input_dtime >= #{start_day}
 and ci.input_dtime <= #{end_day}
 and ci.is_pic = #{is_pic}
order by ci.input_dtime desc

        select count(*) icount,site_id from cs_info_access where app_id ='zwgk'


	        and  access_time >= #{start_day}




  			and  access_time <= #{end_day}



            and site_id in (${site_ids})

        group by site_id order by icount desc

        select count(*) count, author from cs_info where
        final_status=0

            and info_status = #{info_status}


            and cat_id in (${cat_ids})


            and is_host in (${is_host})



			and released_dtime >= #{start_day}




  			and released_dtime <= #{end_day}




  			and is_pic = #{is_pic}


        and site_id = #{site_id}
        group by author having author != '' and author is not null order by count
