mapper.select:
select
id as id, title as title, json_data as jsonData, create_time as createTime, status as status, pub_date as pubDate
from dz_szb
status != -1
AND status = #{status}
order by pub_date desc limit #{start_num,jdbcType=INTEGER},#{page_size,jdbcType=INTEGER}

select id,title,json_data as jsonData,status,create_time as createTime,pub_date as pubDate from dz_szb
status != -1 AND id = #{id}
limit 1

mapper.update:
update dz_szb set
  title = #{title},
  json_data = #{jsonData},
  pub_date = #{pubDate}
where id = #{id}

update dz_szb set
status = #{status}
where id = #{id}
