select * from (
   select row_.*, rownum rownum_ from (       
	   select i.is_host,i.model_id,i.app_id,i.info_id,i.title,f.*,i.content_url,i.site_id,i.cat_id,i.released_dtime,g.doc_no,g.gk_index,g.generate_dtime,g.effect_dtime,g.aboli_dtime,g.topic_key,g.theme_name,g.topic_name,g.gk_input_dept,g.place_key from cs_info i,cs_gk_info g,${table_name} f where i.info_id=g.info_id and g.info_id=f.id  and i.info_status=8 and i.final_status=0 and i.app_id='zwgk'



	   order by f.id asc 
	) row_ where rownum <=(#{start_num,,jdbcType=INTEGER} + #{page_size,,jdbcType=INTEGER})       
) where rownum_ >=(#{start_num,,jdbcType=INTEGER} + 1)


select * from (
   select row_.*, rownum rownum_ from (       
	   select i.is_host,i.model_id,i.app_id,i.info_id,i.title,f.*,i.content_url,i.site_id,i.cat_id,i.released_dtime,g.doc_no,g.gk_index,g.generate_dtime,g.effect_dtime,g.aboli_dtime,g.topic_key,g.theme_name,g.topic_name,g.gk_input_dept,g.place_key from cs_info i,cs_gk_info g,${table_name} f where i.info_id=g.info_id and g.info_id=f.id  and i.info_status=8 and i.final_status=0 and i.app_id=#{app_id}



	   order by f.id asc 
	) row_ where rownum <=(#{start_num,,jdbcType=INTEGER} + #{page_size,,jdbcType=INTEGER})       
) where rownum_ >=(#{start_num,,jdbcType=INTEGER} + 1)

select top ${page_size} i.is_host,i.model_id,i.app_id,i.info_id,i.title,f.*,i.content_url,i.site_id,i.cat_id,i.released_dtime,g.doc_no,g.gk_index,g.generate_dtime,g.effect_dtime,g.aboli_dtime,g.topic_key,g.theme_name,g.topic_name,g.gk_input_dept,g.place_key from cs_info i,cs_gk_info g,${table_name} f where i.info_id=g.info_id and g.info_id=f.id and i.info_status=8 and i.final_status=0 and i.app_id=#{app_id}
and f.id not in (select top ${start_num} f.id from cs_info i,cs_gk_info g,${table_name} f where f.info_id=g.info_id and g.info_id=f.id and i.info_status=8 and i.final_status=0 i.app_id=#{app_id}
order by f.id asc ) order by f.id asc

