select i.released_dtime,i.app_id,i.site_id,i.model_id,i.cat_id,i.is_host,p.pic_id,p.info_id,p.pic_path,p.pic_title,p.pic_note,p.pic_content from cs_info_pic p,cs_info i 
where i.id=p.info_id and i.info_status=8 and i.final_status=0
order by p.pic_id asc  limit #{start_num,,jdbcType=INTEGER},#{page_size,,jdbcType=INTEGER}


select i.released_dtime,i.app_id,i.site_id,i.model_id,i.cat_id,i.is_host,i.title,i.content_url,p.info_id,p.info_content from cs_info_video p,cs_info i 
where i.id=p.info_id and i.info_status=8 and i.final_status=0
order by p.info_id asc  limit #{start_num,,jdbcType=INTEGER},#{page_size,,jdbcType=INTEGER}


select a.att_id ,a.att_cname,a.att_ename,a.att_path,a.upload_dtime,a.app_id,a.site_id from cs_attachment a 
where a.fileext='.doc' or a.fileext='.docx'
order by a.att_id asc  limit #{start_num,,jdbcType=INTEGER},#{page_size,,jdbcType=INTEGER}


select a.att_id ,a.att_cname,a.att_ename,a.att_path,a.upload_dtime,a.app_id,a.site_id from cs_attachment a 
where a.fileext='.pdf' or a.fileext='.PDF'
order by a.att_id asc  limit #{start_num,,jdbcType=INTEGER},#{page_size,,jdbcType=INTEGER}
