mapper.select:
	select * from (
	       select row_.*, rownum rownum_ from (       
	            select re.*,info.title from cs_send_record re,cs_info info  
	            order by ${sort_name} ${sort_type}

	        ) row_ where rownum <=#{page_size,,jdbcType=INTEGER}+#{start_num,,jdbcType=INTEGER}       
    	) where rownum_ >=#{start_num,,jdbcType=INTEGER} + 1

select top ${page_size} re.*,info.title from cs_send_record re,cs_info info 
   and re.id not in
  (
  	select top ${start_num} re.id from cs_send_record re,cs_info info  order by ${sort_name} ${sort_type}
  )
  order by ${sort_name} ${sort_type}

	select * from (
	       select row_.*, rownum rownum_ from (       
	            select * from cs_send_record 
	            where site_id=#{site_id} 
	            order by id desc

	        ) row_ where rownum <=#{page_size,,jdbcType=INTEGER}+#{start_num,,jdbcType=INTEGER}       
    	) where rownum_ >=#{start_num,,jdbcType=INTEGER} + 1

select * from cs_send_record 
            where site_id=#{site_id}
            order by id desc limit #{start_num,,jdbcType=INTEGER},#{page_size,,jdbcType=INTEGER}

mapper.sql:
where send_site_id=#{send_site_id}
 and adopt_status = #{adopt_status}
 and t_site_id in (${t_site_id})
 and send_time > #{start_time}
 and send_time < #{end_time}
