mapper.update:
update cs_ware_category set
	sort_id = #{sort_id}
where id=#{id}

update cs_ware set
	ware_name = 		#{ware_name}
 	,wcat_id = 			#{wcat_id}	
  	,ware_type = 		#{ware_type}	
	,ware_content = 	#{ware_content} 
	,info_num = 		#{info_num}	
	,ware_width = 		#{ware_width}	
	,ware_interval = 	#{ware_interval}	
	,rss_url = 			#{rss_url}	
	,ware_memo = 		#{ware_memo}
	,receive_recom = #{receive_recom}
	where
		id = #{id}

update cs_ware set update_dtime = #{update_dtime}

,next_dtime = #{next_dtime} 

where id=#{id}

update cs_ware  set
	sort_id = #{sort_id}
where id=#{id}

mapper.delete:
delete from cs_ware 
where
	ware_id in (${ware_id}) and site_id = #{site_id}

delete from cs_ware 
where 
	wcat_id in (${id}) and site_id = #{site_id}
