mapper.select:
SELECT c.id AS catId, c.cat_cname AS colname, c.cat_sort AS sortno, ( SELECT sum(hits) FROM cs_info WHERE cat_id IN ( SELECT id FROM cs_info_category WHERE cat_position LIKE concat(c.cat_position, '%') )
and released_dtime <= #{date}
and (publish_source = #{publish_source} or publish_source is null)
) AS tvisitnum, ( SELECT sum(day_hits) FROM cs_info WHERE cat_id IN ( SELECT id FROM cs_info_category WHERE cat_position LIKE concat(c.cat_position, '%')
)
and released_dtime LIKE #{today}
and (publish_source = #{publish_source} or publish_source is null)
) AS visitnum, ( SELECT count(*) FROM cs_info WHERE cat_id IN ( SELECT id FROM cs_info_category WHERE cat_position LIKE concat(c.cat_position, '%') )
and released_dtime <= #{date}
and (publish_source = #{publish_source} or publish_source is null)
) AS tpubnum, ( SELECT count(*) FROM cs_info WHERE cat_id IN ( SELECT id FROM cs_info_category WHERE cat_position LIKE concat(c.cat_position, '%') )
and released_dtime LIKE #{today}
and (publish_source = #{publish_source} or publish_source is null)
) AS pubnum FROM cs_info_category c WHERE c.parent_id = 0 AND c.site_id LIKE 'CMS%' ORDER BY c.cat_id ASC

select
info_id,title,subtitle,top_title,author,editor,source,hits,input_dtime,thumb_url,content_url,released_dtime from
cs_info where site_id like 'CMS%'
 and released_dtime >= #{start_day}
 and released_dtime <= #{end_day}
order by hits desc limit 0,10

select
info_id,title,subtitle,top_title,author,editor,source,hits,input_dtime,thumb_url,content_url,released_dtime from
cs_info where site_id like 'CMS%'
 and released_dtime >= #{start_day}
 and released_dtime <= #{end_day}
order by hits desc

select c.cat_cname,c.cat_id from cs_info_category c LEFT JOIN dz_cdgjj_source_info_category s on s.cat_id = c.cat_id where 1=1
 and c.parent_id = #{colid}
 and s.source = #{source}

        select * from (
        select row_.*, rownum rownum_ from (
        select
        info_id,title,subtitle,top_title,author,editor,source,hits,input_dtime,thumb_url,content_url,released_dtime from
        cs_info
        where info_status = 8 and final_status = 0 and site_id like 'CMS%'
        and cat_id = #{cat_id}
        and publish_source like #{publish_source}
        order by released_dtime desc

		        ) row_ where rownum <=#{limit,,jdbcType=INTEGER}+#{start,,jdbcType=INTEGER}
	    	) where rownum_ >=#{start,,jdbcType=INTEGER} + 1

select count(*) total from cs_info
where info_status = 8 and final_status = 0 and site_id like 'CMS%'
and cat_id = #{cat_id}
and publish_source like #{publish_source}

select
info_id,title,subtitle,top_title,author,editor,source,hits,input_dtime,thumb_url,content_url,released_dtime from
cs_info
where info_status = 8 and final_status = 0 and site_id like 'CMS%'
and cat_id = #{cat_id}
and publish_source like #{publish_source}
order by released_dtime desc limit #{start,,jdbcType=INTEGER},#{limit,,jdbcType=INTEGER}
