mapper.select:
select * from cs_gk_sequence 


		seq_item = #{seq_item}


		and seq_type = #{seq_type}


		and seq_ymd = #{seq_ymd}


		and site_id = #{site_id}

mapper.update:
update cs_gk_sequence set
	seq_cur_value = seq_cur_value+1
where id = #{id}

update cs_gk_sequence set
	seq_cur_value = 1
where id = #{id}
