mapper.select:
    select count(ysq_id) from cs_gk_ysq 


					and site_id = '${site_id}'


					and node_id = '${node_id}'


					and do_state = ${do_state}


					and final_status = ${final_status}



					and ysq_type = ${ysq_type}


					and put_dtime > '${put_dtime}'


				    and ${con_name} like '%${con_value}%'


					and content like '%${content}%'


				 	${highSearchString}

mapper.update:
update cs_gk_ysq
is_other = #{is_other}, is_third = #{is_third}, is_extend = #{is_extend}, accept_content = #{accept_content}, accept_dtime = #{accept_dtime}, reply_content = #{reply_content}, reply_dtime = #{reply_dtime}, accept_user = #{accept_user}, reply_user = #{reply_user}, reply_type = #{reply_type}, is_mail = #{is_mail}, do_state = #{do_state}, final_status = #{final_status}, publish_state = #{publish_state}, supervise_flag = #{supervise_flag}, weight = #{weight}, node_id = #{node_id}, node_name = #{node_name}, file_path = #{file_path}
ysq_id = ${ysq_id}

update cs_gk_ysq
query_code=#{query_code}, ysq_type=#{ysq_type}, name=#{name}, company=#{company}, card_name=#{card_name}, card_code=#{card_code}, org_code=#{org_code}, licence=#{licence}, legalperson=#{legalperson}, linkman=#{linkman}, tel=#{tel}, fax=#{fax}, phone=#{phone}, email=#{email}, postcode=#{postcode}, address=#{address}, put_dtime=#{put_dtime}, content=#{content}, gk_index=#{gk_index}, description=#{description}, is_derate=#{is_derate}, offer_type=#{offer_type}, get_method=#{get_method}, is_other=#{is_other}, is_third=#{is_third}, is_extend=#{is_extend}, accept_content=#{accept_content}, accept_dtime=#{accept_dtime}, accept_user=#{accept_user}, reply_type=#{reply_type}, reply_content=#{reply_content}, reply_dtime=#{reply_dtime}, reply_user=#{reply_user}, is_mail=#{is_mail}, do_state=#{do_state}, final_status=#{final_status}, publish_state=#{publish_state}, supervise_flag=#{supervise_flag}, time_limit=#{time_limit}, timeout_flag=#{timeout_flag}, lasthit_dtime=#{lasthit_dtime}, weight=#{weight}, weight=#{file_path}
ysq_id = ${ysq_id}

mapper.select:


	select * from (
	       select row_.*, rownum rownum_ from(       
	            select ysq_id,ysq_code,ysq_type,name,node_name,put_dtime,publish_state,is_third,is_extend,timeout_flag,do_state,reply_dtime     
	             from cs_gk_ysq



	           order by ${sort_name} ${sort_type}
	        ) row_ where rownum <=#{page_size,,jdbcType=INTEGER}+#{start_num,,jdbcType=INTEGER}       
    	) where rownum_ >=#{start_num,,jdbcType=INTEGER} + 1

select ysq_id,ysq_code,ysq_type,name,node_name,put_dtime,publish_state,is_third,is_extend,timeout_flag,do_state,reply_dtime    
             from cs_gk_ysq

		order by ${sort_name} ${sort_type} limit #{start_num,,jdbcType=INTEGER},#{page_size,,jdbcType=INTEGER}
