mapper.select:
select * from cp_sq where sq_id = #{sq_id}


        and (me_id = #{me_id} or (publish_status = 1 and is_open = 1))


        and sq_flag in (0,1,2) and publish_status = 1 and is_open = 1

select count(sq_id) from cp_sq where model_id in (${model_id})	and sq_status != '-99'






                and (
                (do_dept is null or do_dept = 0)

                    or do_dept in (${do_dept})

                )







                ${dsj_sql}

            and do_dept in (${do_dept})




and publish_status = ${publish_status}


    and sq_status in (${sq_status})


    and sq_flag in (${sq_flag})



    and is_back = #{is_back}


    and alarm_flag in (${alarm_flag})

and limit_flag = ${limit_flag}
and supervise_flag = ${supervise_flag}


    and prove_type in (${prove_type})



and pur_id = ${pur_id}
and submit_name like '%${submit_name}%'
and sq_dtime > #{sq_dtime}
and sq_title2 like '%${sq_title2}%'
and sq_realname like '%${sq_realname}%'
and sq_content2 like '%${sq_content2}%'
and sq_code = #{sq_code}

select count(distinct cs.sq_id) from cp_sq cs,cp_process cp	where cs.sq_id = cp.sq_id and cs.model_id in (${model_id}) and cp.do_dept = #{do_dept}
and cp.pro_type in (${pro_type})

    and sq_dtime > #{sq_dtime}

and pur_id = ${pur_id}
and submit_name like '%${submit_name}%'
and cs.sq_title2 like '%${sq_title2}%'
and cs.sq_realname like '%${sq_realname}%'
and cs.sq_content2 like '%${sq_content2}%'

mapper.update:
