mapper.select:
select cat_id,gk_type,
site_id,
count(cat_id) count from( select c.info_id info_id, c.site_id site_id, c.cat_id cat_id, c.app_id app_id , g.gk_type gk_type from cs_info c right join cs_gk_info g on c.info_id=g.info_id where c.final_status=0 and c.is_host=0 and c.info_status=8
and c.site_id = #{site_id}
and released_dtime >= #{start_day}
and released_dtime <= #{end_day}
) group by cat_id,gk_type
,site_id

select  sum(info_count) info_count,
		sum(z_count) z_count,
		sum(b_count) b_count,
		sum(y_count) y_count,
		cat_id 
	from cs_gk_count

	site_id = #{site_id}

		and cat_id = #{cat_id}




		and update_time >= #{start_day}




		and update_time <= #{end_day}



	group by cat_id

select sum(info_count) info_count, sum(z_count) z_count, sum(b_count) b_count, sum(y_count) y_count, site_id from cs_gk_count
and update_time >= #{start_day}
and update_time <= #{end_day}
and site_id in (${site_ids})
group by site_id

mapper.update:
update cs_gk_count set
	info_count = #{info_count},
	z_count = #{z_count},
	y_count = #{y_count},
	b_count = #{b_count},
	app_id = #{app_id}
where 
	cat_id = #{cat_id}
	and update_time = #{update_time}
	and site_id = #{site_id}

mapper.delete:
delete from cs_gk_count
where
	update_time = #{update_time}		


		and site_id = #{site_id}



		and cat_id in (${cat_id})

mapper.select:
	     select count(i.info_id) info_count,i.site_id from cs_info i,cs_gk_info g 
         where i.info_status=8 and i.info_id=g.info_id


				and g.gk_type = #{gk_type}




				and i.released_dtime >= #{start_day}




				and i.released_dtime <= #{end_day}



				and i.site_id in (${site_ids})



				and i.is_pic = #{is_pic}


         group by i.site_id order by info_count desc

	     select count(i.info_id) info_count,i.cat_id from cs_info i,cs_gk_info g 
         where i.info_status=8  and i.info_id=g.info_id and site_id = #{site_id} 


				and g.gk_type=#{gk_type}




				and released_dtime >= #{start_day}




				and released_dtime <= #{end_day}




				and i.is_pic = #{is_pic}


         group by i.cat_id
