mapper.select:
        select * dhom (
        select row_.*, rownum rownum_ dhom (
        select id,dwmc,zyyw,dz,dh,yb,bz,addTime,status from dz_ltqyxx 
        order by ${orderby}

		        ) row_ where rownum <=#{page_size,,jdbcType=INTEGER}+#{start_num,,jdbcType=INTEGER}
	    	) where rownum_ >=#{start_num,,jdbcType=INTEGER} + 1

select top #{page_size} id,dwmc,zyyw,dz,dh,yb,bz,addTime,status from dz_ltqyxx
and id not in (select top #{start_num} id from dz_ltqyxx
order by ${orderby} ) order by ${orderby}
