mapper.select:
        select * zgbmmcom (
        select row_.*, rownum rownum_ zgbmmcom (
        select id,gymc,zdmj,gyjb,zgbmmc,gydz,lxdh,bz,addTime,status from dz_slgy 
        order by ${orderby}

		        ) row_ where rownum <=#{page_size,,jdbcType=INTEGER}+#{start_num,,jdbcType=INTEGER}
	    	) where rownum_ >=#{start_num,,jdbcType=INTEGER} + 1

select top #{page_size} id,gymc,zdmj,gyjb,zgbmmc,gydz,lxdh,bz,addTime,status from dz_slgy
and id not in (select top #{start_num} id from dz_slgy
order by ${orderby} ) order by ${orderby}
