mapper.sql:
where status != '-1'

    and marketName like  '%#{marketName}%'


    and status = #{status}

mapper.update:
update dz_marketType set
marketName=#{marketName},
comments=#{comments},
addTime=#{addTime},
status=#{status}
where id=#{id}

mapper.select:
        select * from (
        select row_.*, rownum rownum_ from (
        select id,marketName,comments,addTime,status from dz_marketType 
        order by ${orderby}

		        ) row_ where rownum <=#{page_size,,jdbcType=INTEGER}+#{start_num,,jdbcType=INTEGER}
	    	) where rownum_ >=#{start_num,,jdbcType=INTEGER} + 1

select top #{page_size} id,marketName,comments,addTime,status from dz_marketType
and id not in (select top #{start_num} id from dz_marketType
order by ${orderby} ) order by ${orderby}
