mapper.select:
        select * qyxxmom (
        select row_.*, rownum rownum_ qyxxmom (
        select id,scs,cpmc,cpbh,qyxxm,hzcl,mj,xkqx,bz,addTime,status from dz_lsspzs 
        order by ${orderby}

		        ) row_ where rownum <=#{page_size,,jdbcType=INTEGER}+#{start_num,,jdbcType=INTEGER}
	    	) where rownum_ >=#{start_num,,jdbcType=INTEGER} + 1

select top #{page_size} id,scs,cpmc,cpbh,qyxxm,hzcl,mj,xkqx,bz,addTime,status from dz_lsspzs
and id not in (select top #{start_num} id from dz_lsspzs
order by ${orderby} ) order by ${orderby}
