mapper.select:
        select * from (
        select row_.*, rownum rownum_ from (
        select id,qx,qymc,jydz,fr,lxdh,bz,addTime,status from dz_syjyqy 
        order by ${orderby}

		        ) row_ where rownum <=#{page_size,,jdbcType=INTEGER}+#{start_num,,jdbcType=INTEGER}
	    	) where rownum_ >=#{start_num,,jdbcType=INTEGER} + 1

select top #{page_size} id,qx,qymc,jydz,fr,lxdh,bz,addTime,status from dz_syjyqy
and id not in (select top #{start_num} id from dz_syjyqy
order by ${orderby} ) order by ${orderby}
